mapper.select:
SELECT R.* FROM (SELECT @ROWNUM := @ROWNUM +1 AS ROWNUM, R.RECRUIT_NO, R.RECRUIT_TITLE, R.RECRUIT_VIEWCOUNT, date_format(R.RECRUIT_START, '%Y-%m-%d')as RECRUIT_START, date_format(R.RECRUIT_END, '%Y-%m-%d')as RECRUIT_END, date_format(R.RECRUIT_DATE, '%Y-%m-%d')as RECRUIT_DATE, R.CAREER, R.CONTENTS_STATUS FROM recruit R,(SELECT @ROWNUM := 0) TMP WHERE RECRUIT_DLE_STATUS = 'N'
AND R.RECRUIT_TITLE like concat ('%',#{search},'%')
ORDER BY CASE WHEN CONTENTS_STATUS IN ('Y', 'R') THEN 0 ELSE 1 END, RECRUIT_NO DESC) R LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE recruit
SET RECRUIT_DLE_STATUS = 'Y'
WHERE RECRUIT_NO = #{recruitNum}

UPDATE recruit SET
RECRUIT_FILE_NAME_ORG = #{orgFileName}, RECRUIT_FILE_NAME = #{fileName}, RECRUIT_FILE_PATH = #{filePath}
RECRUIT_FILE_NAME_ORG = null, RECRUIT_FILE_NAME = null, RECRUIT_FILE_PATH = null
WHERE RECRUIT_NO = #{recruitNo}
